public class VolatileTest {

    private static volatile boolean flag ;

    public static void main(String[] args) throws InterruptedException {
        new Thread(new Runnable() {
            @Override
            public void run() {
                for(;;) {
                    if (flag == !flag) {
                        System.out.println("end !");
                        System.exit(1);
                    }
                }
            }
        }).start();
        Thread.sleep(1);
        new Thread(new Runnable() {
            @Override
            public void run() {
                for(;;) {
                    flag = !flag;
                }
